  [TKeys.INVOCATION_JEWELS_TITLE]: 'Permata Dzikir Sunni dalam Menyikap Tabir yang Tersembunyi',
  [TKeys.INVOCATION_OVERFLOW_TITLE]:
    "Fayd Rohmani (Limpahan Rahmat) dan Imdad Nurany (Pembekalan Cahaya)\ndalam Sholawat atas Nabi Al-'Adnani",
  [TKeys.INVOCATION_AHZABS_TITLE]: 'Ahzabs',
  [TKeys.INVOCATION_NIVEAU_TITLE]: 'مقامات ذكر السلوك',
  [TKeys.INVOCATION_NIVEAU_1]: '',
